import React, {Component} from 'react';
import './Counter.css';

class App extends Component {
    state = {
        counter: 0
    };
    timesClicked = (event) => {
      this.setState({ counter: this.state.counter + 1 });
  }
  
  render() {
      return (<div>
        <div>
          <p> { this.state.counter } </p>
          <button onClick = { this.timesClicked } > Show me something! </button>
        </div>
          
        
      </div>
    );
  }
}
