import React, {Component} from 'react'; /*nimi*/
import './Counter.css'; /*polun kanssa */

class Counter extends Component {
     state = {
        counter: 100 /* voi lis채t채 muitakin pilkulla erotettuna*/
    }
    
    addHandler = () => {
      this.setState ({
        counter: this.state.counter + 1
        })
    }
      
  render() {
    return (<div className="container">
        <div className = 'circle'>
          <p className = 'number'>{this.state.counter}</p>
        </div> 

        <div className = 'buttons'>  
      <button onClick = { this.timesClicked } > Add One! </button>        
      <button onClick = { this.timesClicked } > Remove One! </button>
      <button onClick = { this.timesClicked } > Reset to 100 </button>
    </div>       
    </div>
    );
  
    

            

}
}


export default Counter;
