import React, {Component} from 'react';
import './Counter.css';

class Counter extends Component {
    state = {
        counter: 0
    };
      timesClicked = (event) => {
        this.setState({ counter: this.state.counter + 1 });
      }
  
  render() {
    return (<div>
        <div>
          <p> { this.state.counter } </p>
          <button onClick = { this.timesClicked } > Add One! </button>
        </div>        
    </div>
    );
  }

  timesClicked = (event) => {
    this.setState({ counter: this.state.counter - 1 });
  }

render() {
return (<div>
    <div>
      <p> { this.state.counter } </p>
      <button onClick = { this.timesClicked } > Remove One! </button>
    </div>        
</div>
);
}
}

export default Counter;
